contract STAKING is Ownable {
    ERC20 public stakingToken;
    
    uint256 public rewardRate1Days = 5;  //   5% APR Reward
    uint256 public rewardRate7Days = 50; //  50% APR Reward
    uint256 public rewardRate30Days = 210; //  210% APR Reward
    uint256 public rewardRate90Days = 1000; // 1000% APR Reward

    struct Stake {
        uint256 amount;
        uint256 startTime;
        uint256 endTime;
        uint256 rewardRate;
        bool withdrawn;
    }

    mapping(address => Stake[]) public stakes;

    event Staked(address indexed user, uint256 amount, uint256 lockPeriod, uint256 rewardRate);
    event Unstaked(address indexed user, uint256 amount, uint256 reward);
    
    constructor(ERC20 _stakingToken) {
        stakingToken = _stakingToken;
    }

    function stake(uint256 _amount, uint256 _lockPeriod) external  {
        require(_amount >= 3000e18, "MineX: Cannot stake  less than 3000 tokens");
        require(
            _lockPeriod == 1 days ||
            _lockPeriod == 7 days ||
            _lockPeriod == 30 days ||
            _lockPeriod == 90 days,
            "MineX: Invalid lock period"
        );

        uint256 rewardRate = getRewardRate(_lockPeriod);

        stakingToken.transferFrom(msg.sender, address(this), _amount);

        stakes[msg.sender].push(
            Stake({
                amount: _amount,
                startTime: block.timestamp,
                endTime: block.timestamp + _lockPeriod,
                rewardRate: rewardRate,
                withdrawn: false
            })
        );

        emit Staked(msg.sender, _amount, _lockPeriod, rewardRate);
    }

    function unstake(uint256 _stakeIndex) external  {
        require(_stakeIndex < stakes[msg.sender].length, "MineX: Invalid stake index");
        Stake storage userStake = stakes[msg.sender][_stakeIndex];
        require(block.timestamp >= userStake.endTime, "wait for unlocked");
        require(!userStake.withdrawn, "MineX: Stake already withdrawn");

        uint256 amountToWithdraw = userStake.amount;
        uint256 reward = calculateReward(msg.sender, _stakeIndex);
        stakingToken.mint(address(this), reward);

        userStake.withdrawn = true;
        stakingToken.transfer(msg.sender, amountToWithdraw + reward);

        emit Unstaked(msg.sender, amountToWithdraw, reward);
    }

    function getUserStakes(address _user) external view returns (Stake[] memory) {
        return stakes[_user];
    }

    function setRewardRate(
        uint256 _rewardRate1Days,
        uint256 _rewardRate7Days,
        uint256 _rewardRate30Days,
        uint256 _rewardRate90Days) external  onlyOwner {
            
        rewardRate1Days = _rewardRate1Days;
        rewardRate7Days = _rewardRate7Days;
        rewardRate30Days = _rewardRate30Days;
        rewardRate90Days = _rewardRate90Days;
    }

    function getRewardRate(uint256 _lockPeriod) public view returns (uint256) {
        if (_lockPeriod == 1 days) return rewardRate1Days;
        if (_lockPeriod == 7 days) return rewardRate7Days;
        if (_lockPeriod == 30 days) return rewardRate30Days;
        if (_lockPeriod == 90 days) return rewardRate90Days;
        revert("MineX: Invalid lock period");
    }

    function calculateReward(address _user, uint256 _stakeIndex) public view returns (uint256) {
        Stake memory userStake = stakes[_user][_stakeIndex];
        return (userStake.amount * userStake.rewardRate) / 100;
    }
}

